var TS = require('..');

exports.testFormalField = function(test) {
    var formalStringField = TS.fString;

    test.equal(formalStringField.getFieldType(), 'formal');
    test.equal(formalStringField.getType(), 'string');

    test.done();
};

exports.testActualField = function(test) {
    var actualStringField = TS.createActualField('string', 'Adam');

    test.equal(actualStringField.getFieldType(), 'actual');
    test.equal(actualStringField.getType(), 'string');
    test.equal(actualStringField.getValue(), 'Adam');
    test.done();
};