var TS = require('..');

exports.testTuple = function testTuple(test) {
    var field1 = new TS.createActualField('string', 'Test');
    var field2 = new TS.createActualField('boolean', true);
    var field3 = new TS.createActualField('integer', 42);
    var tuple = new TS.Tuple([field1, field2, field3]);

    test.equals(tuple.getFieldCount(), 3);
    test.equals(tuple.getField(0).getValue(), 'Test');
    test.equals(tuple.getField(1).getValue(), true);
    test.equals(tuple.getField(2).getValue(), 42);
    test.done();
};